        // Lexical cast

        /// Convert a flag into an integer value  typically binary flags
        inline int64_t to_flag_value(std::string val)
        {
            static const std::string trueString("true");
            static const std::string falseString("false");
            if (val == trueString)
            {
                return 1;
            }
            if (val == falseString)
            {
                return -1;
            }
            val = detail::to_lower(val);
            int64_t ret;
            if (val.size() == 1)
            {
                switch (val[0])
                {
                case '0':
                case 'f':
                case 'n':
                case '-':
                    ret = -1;
                    break;
                case '1':
                case 't':
                case 'y':
                case '+':
                    ret = 1;
                    break;
                case '2':
                case '3':
                case '4':
                case '5':
                case '6':
                case '7':
                case '8':
                case '9':
                    ret = val[0] - '0';
                    break;
                default:
                    throw std::invalid_argument("unrecognized character");
                }
                return ret;
            }
            if (val == trueString || val == "on" || val == "yes" || val == "enable")
            {
                ret = 1;
            }
            else if (val == falseString || val == "off" || val == "no" || val == "disable")
            {
                ret = -1;
            }
            else
            {
                ret = std::stoll(val);
            }
            return ret;
        }

        /// Signed integers
        template<
            typename T,
            enable_if_t<std::is_integral<T>::value && std::is_signed<T>::value && !is_bool<T>::value && !std::is_enum<T>::value,
                        detail::enabler> = detail::dummy>
        bool lexical_cast(std::string input, T& output)
        {
            try
            {
                size_t n = 0;
                long long output_ll = std::stoll(input, &n, 0);
                output = static_cast<T>(output_ll);
                return n == input.size() && static_cast<long long>(output) == output_ll;
            }
            catch (const std::invalid_argument&)
            {
                return false;
            }
            catch (const std::out_of_range&)
            {
                return false;
            }
        }

        /// Unsigned integers
        template<typename T,
                 enable_if_t<std::is_integral<T>::value && std::is_unsigned<T>::value && !is_bool<T>::value, detail::enabler> =
                     detail::dummy>
        bool lexical_cast(std::string input, T& output)
        {
            if (!input.empty() && input.front() == '-')
                return false; // std::stoull happily converts negative values to junk without any errors.

            try
            {
                size_t n = 0;
                unsigned long long output_ll = std::stoull(input, &n, 0);
                output = static_cast<T>(output_ll);
                return n == input.size() && static_cast<unsigned long long>(output) == output_ll;
            }
            catch (const std::invalid_argument&)
            {
                return false;
            }
            catch (const std::out_of_range&)
            {
                return false;
            }
        }

        /// Boolean values
        template<typename T, enable_if_t<is_bool<T>::value, detail::enabler> = detail::dummy>
        bool lexical_cast(std::string input, T& output)
        {
            try
            {
                auto out = to_flag_value(input);
                output = (out > 0);
                return true;
            }
            catch (const std::invalid_argument&)
            {
                return false;
            }
        }

