// These are additional cases which the official break tests don't cover:
static constexpr GraphemeBreakTest s_graphemeBreakTestsExtra[] = {
    { L"multiple combining marks", L"a\u0363", L"e\u0364\u0364", L"i\u0365" },
    { L"multiple US flag regional indicators", L"\U0001F1FA\U0001F1F8", L"\U0001F1FA\U0001F1F8" },
};

static constexpr std::span<const GraphemeBreakTest> s_graphemeBreakTestsAll[] = {
    s_graphemeBreakTests,
    s_graphemeBreakTestsExtra,
};

class CodepointWidthDetectorTests
{
    TEST_CLASS(CodepointWidthDetectorTests);

    TEST_METHOD(GraphemeBreakTest)
    {
        WEX::TestExecution::DisableVerifyExceptions disableVerifyExceptions{};
        WEX::TestExecution::SetVerifyOutput verifyOutputScope{ WEX::TestExecution::VerifyOutputSettings::LogOnlyFailures };

        auto& cwd = CodepointWidthDetector::Singleton();
        std::vector<std::wstring_view> expected;
        std::vector<std::wstring_view> actual;
        std::wstring text;

        for (const auto& tests : s_graphemeBreakTestsAll)
        {
            for (const auto& test : tests)
            {
                expected.clear();
                for (const auto g : test.graphemes)
                {
                    if (!g)
                    {
                        break;
                    }
                    expected.emplace_back(g);
                }

                text.clear();
                for (const auto& g : expected)
                {
                    text.append(g);
                }

                actual.clear();
                for (GraphemeState state;;)
                {
                    const auto ok = cwd.GraphemeNext(state, text);
                    actual.emplace_back(state.beg, state.len);
                    if (!ok)
                    {
                        break;
                    }
                }
                VERIFY_ARE_EQUAL(expected, actual, test.comment);

                actual.clear();
                for (GraphemeState state;;)
                {
                    const auto ok = cwd.GraphemePrev(state, text);
                    actual.emplace_back(state.beg, state.len);
                    if (!ok)
                    {
                        break;
                    }
                }
                std::reverse(actual.begin(), actual.end());
                VERIFY_ARE_EQUAL(expected, actual, test.comment);
            }
        }
    }

    TEST_METHOD(BasicGraphemes)
    {
        static constexpr std::wstring_view text{ L"a\u0363e\u0364\u0364i\u0365" };

        auto& cwd = CodepointWidthDetector::Singleton();

        const std::vector<size_t> expectedAdvances{ 2, 3, 2 };
        const std::vector<int> expectedWidths{ 1, 1, 1 };
        std::vector<size_t> actualAdvances;
        std::vector<int> actualWidths;

        for (GraphemeState state;;)
        {
            const auto ok = cwd.GraphemeNext(state, text);
            actualAdvances.emplace_back(state.len);
            actualWidths.emplace_back(state.width);
            if (!ok)
            {
                break;
            }
        }

        VERIFY_ARE_EQUAL(expectedAdvances, actualAdvances);
        VERIFY_ARE_EQUAL(expectedWidths, actualWidths);

        actualAdvances.clear();
        actualWidths.clear();

        for (GraphemeState state;;)
        {
            const auto ok = cwd.GraphemePrev(state, text);
            actualAdvances.emplace_back(state.len);
            actualWidths.emplace_back(state.width);
            if (!ok)
            {
                break;
            }
        }

        std::reverse(actualAdvances.begin(), actualAdvances.end());
        std::reverse(actualWidths.begin(), actualWidths.end());

        VERIFY_ARE_EQUAL(expectedAdvances, actualAdvances);
        VERIFY_ARE_EQUAL(expectedWidths, actualWidths);
    }

    TEST_METHOD(ChunkedText)
    {
        struct Test
        {
            TextMeasurementMode mode;
            std::vector<int> advancesNext;
            std::vector<int> widthsNext;
            std::vector<int> advancesPrev;
            std::vector<int> widthsPrev;
        };
        const std::array tests{
            Test{
                .mode = TextMeasurementMode::Graphemes,
                .advancesNext = { 3, 3, 0, 1 },
                .widthsNext = { 2, 2, 2, 1 },
                .advancesPrev = { 1, 0, 3, 3 },
                .widthsPrev = { 1, 1, 2, 2 },

            },
            Test{
                .mode = TextMeasurementMode::Wcswidth,
                .advancesNext = { 3, 1, 2, 0, 1 },
                .widthsNext = { 1, 1, 2, 2, 1 },
                .advancesPrev = { 1, 0, 2, 1, 3 },
                .widthsPrev = { 1, 1, 2, 0, 1 },
            },
            Test{
                .mode = TextMeasurementMode::Console,
                .advancesNext = { 2, 1, 0, 1, 2, 0, 1 },
                .widthsNext = { 1, 0, 0, 0, 2, 2, 1 },
                .advancesPrev = { 1, 0, 2, 1, 0, 1, 2 },
                .widthsPrev = { 1, 1, 2, 0, 0, 0, 1 },
            },
        };

        // That's a fully qualified rainbow flag followed by a single "a" character.
        static constexpr std::array chunks{
            std::wstring_view{ L"\U0001F3F3\uFE0F" },
            std::wstring_view{ L"\u200D\U0001F308" },
            std::wstring_view{ L"a" },
        };

        CodepointWidthDetector cwd;
        GraphemeState state;
        std::vector<int> actualAdvances;
        std::vector<int> actualWidths;

        for (const auto& test : tests)
        {
            cwd.Reset(test.mode);

            state = {};
            actualAdvances.clear();
            actualWidths.clear();

            for (int i = 0; i < 3; i++)
            {
                bool ok;
                do
                {
                    ok = cwd.GraphemeNext(state, chunks[i]);
                    actualAdvances.emplace_back(state.len);
                    actualWidths.emplace_back(state.width);
                } while (ok);
            }

            VERIFY_ARE_EQUAL(test.advancesNext, actualAdvances);
            VERIFY_ARE_EQUAL(test.widthsNext, actualWidths);

            state = {};
            actualAdvances.clear();
            actualWidths.clear();

            for (int i = 2; i >= 0; i--)
            {
                bool ok;
                do
                {
                    ok = cwd.GraphemePrev(state, chunks[i]);
                    actualAdvances.emplace_back(state.len);
                    actualWidths.emplace_back(state.width);
                } while (ok);
            }

            VERIFY_ARE_EQUAL(test.advancesPrev, actualAdvances);
            VERIFY_ARE_EQUAL(test.widthsPrev, actualWidths);
        }
    }
};
